CREATE DATABASE game;

CREATE TABLE Triangles (
  x SMALLINT(2048),
  y SMALLINT(2048),
  type TINYTEXT
)
	
CREATE TABLE Characters (
  name TINYTEXT,
  category TINYTEXT,
  baseoffense SMALLINT(2048), --Athletics (climb, force open, grapple, high jump, long jump, reposition, shove, swim, trip; disarm)
  basedefense SMALLINT(2048), --n/a
  basespeed SMALLINT(2048), --Acrobatics (balance, tumble through; fly maneuver, squeeze), Piloting, Stealth (conceal object, hide, sneak), Thievery (palm object, steal; disable device, pick lock)
  basemental SMALLINT(2048), --Arcana (recall knowledge; borrow an arcane spell, decipher writing, identify magic, learn a spell), Computers, Crafting (recall knowledge, repair; craft, earn income, identify alchemy), Deception (create diversion, impersonate, lie; feint), Diplomacy (gather information, make impression, request), Intimidate (coerce, demoralize), Lore (recall knowledge; earn income), Medicine (first aid; disease, poison, wounds), Nature (command animal, recall knowledge; identify magic, learn a spell), Occultism (recall knowledge; decipher writing, identify magic, learn a spell), Society (recall knowledge, subsist; create forgery, decipher writing), Survival (sense direction, subsist; cover tracks, track), Religion (recall knowledge; decipher writing, identify magic, learn a spell), Society (recall knowledge, subsist; create forgery)
  offense SMALLINT(2048), 
  defense SMALLINT(2048),
  speed SMALLINT(2048), 
  mental SMALLINT(2048)
  crouching BOOLEAN,
  x INT(1000092),
  y INT(1000092),
  dX TINYINT(128),
  dY TINYINT(128)
);

CREATE TABLE Dialogue {
  textvalue TINYTEXT,
  category TINYTEXT,
  x SMALLINT(2048),
  y SMALLINT(2048),
}

CREATE TABLE Weapons (
  name TINYTEXT,
  offense_mod TINYINT(128), --Log2((avg damage)*critical)/2)
  speed_mod TINYINT(128), --Log2((range/20)-bulk)
);

CREATE TABLE Armor (
  name TINYTEXT,	
  defense_mod TINYINT(128), --Log2(EACbonus+KACbonus)  
  speed_mod TINYINT(128) --Log2(maxdexbonus-armorcheckpenalty-speedadjustment-bulk)    
);

